import { Button, Input } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName } from "../../slices/userSlice";
import { PlayerContainer } from "../../styles/cards";

export function PlayerCard(user, index) {
    const [name, setName] = useState("");
    const users = useSelector((state) => state.users);
    const dispatch = useDispatch();
    const [edit, setEdit] = useState(true)

    const changeName = (userIndex) => {
        dispatch(addUserName({
            name: name,
            index: userIndex
        }));
        setEdit(false)

    };
    function inputName(event) {
        const userName = event.target.value
        return setName(userName)
    }

    return (
        <PlayerContainer>

            {edit &&
                <Box>
                    <Input onChange={(event) => inputName(event)}></Input>

                    <Button onClick={() => changeName(index)}>âˆš</Button>
                </Box>
            }
            {!edit &&
                <Box>
                    <h3>{name}</h3>
                    <Button>/</Button>
                </Box>
            }

            <div>{user.score}</div>
            <Button>Win</Button>
            <Button>Finished phase</Button>
            <h3>Phase {user.phase}</h3>
        </PlayerContainer>
    )
}
