const { createSlice } = require("@reduxjs/toolkit");

const initialState = {round: 1, roundWinner: null, finishGame : false, modalWindow: false}

const gameSlice = createSlice({
    name: "game",
    initialState,
    reducers: { 
        roundWinner(state, action) {
            return {...state, roundWinner: action.payload}
        },
        nextRound(state) {
            return {...state, round: state.round + 1}
        },
        finishGame(state) {
            return {...state, finishGame: true}
        },
        modalWindowIsOpen(state, {payload}) {
            return {...state, modalWindow: payload}
        }
    }
});

export default gameSlice.reducer;
export const { modalWindowIsOpen, roundWinner, nextRound, finishGame } = gameSlice.actions
