import styled from "@emotion/styled";
import { Button, Grid, Input, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";

import theme, { Colors } from ".";
import CheckMarkImage from "../images/checkmark.svg";
import EditImage from "../images/edit.svg"


export const CardsContainer = styled(Box)((theme) => ({
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
    color: Colors.primary,
    height: "100%",
    margin: "30px 0px",
    [theme.breakpoints.down("sm")]: {
        display: "flex",
        flexWrap: "wrap"
    }


}));

export const PlayerContainer = styled(Box)(({theme}) => ({
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "space-around",
    background: Colors.body_bg,
    border: "1px solid black",
    borderRadius: "15px",
    height: "500px",
    width: "400px",
    color: Colors.primary,
    margin: "0px 5px",
    [theme.breakpoints.down("md")]: {
        height: "350px"
    },
    [theme.breakpoints.down("sm")]: {
        height: "200px",
        width: "270px",
        justifyContent: "center",

    }
}));

export const NameContainer = styled(Box)(() => ({
    marginLeft: "50px",
    display: "flex",

}));

export const EditNameContainer = styled(Box)(({theme}) => ({
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
    [theme.breakpoints.down("sm")]: {
        size: "small"
    }
}))


export const CardsButtons = styled(Button)(({theme}) => ({
    background: "#7A79B3",
    border: "0.5px solid #9093C4",
    borderRadius: "15px",
    width: "fit-content",
    color: Colors.primary,
    "&:hover": {
        background: Colors.secondary
    },
    [theme.breakpoints.down("sm")]:{
        height: "25px",
        marginTop: "3px"

    }
}));

export const InputEdit = styled(TextField)(() => ({
    background: "rgb(250, 244, 233)",
    marginLeft: "60px",
    borderRadius: "15px",
    padding: "10px 5px",
    height: "25px"


}));

export const CheckMarkContainer = styled(Box)(() => ({
    height: "30px",
    width: "30px",
    backgroundSize: "cover",
    backgroundImage: `url(${CheckMarkImage})`,

}));

export const EditIconContainer = styled(Box)(() => ({
    height: 20,
    width: 20,
    backgroundSize: "cover",
    backgroundImage: `url(${EditImage})`,

}));

export const ScoreValue = styled(Typography)(({theme}) => ({
    fontSize: "60px",
    fontWeight: "bold",
    [theme.breakpoints.down("sm")]: {
        fontSize: "30px"
    }
}));
