import { Button } from "@mui/material";
import { Box } from "@mui/system";
import { useDispatch } from "react-redux";
import { nextRound, roundWinner } from "../../slices/gameSlice";
import { updatePhaseForNewRound } from "../../slices/userSlice";



export default function Footer() {
const dispatch = useDispatch();

const startNextRound = () => {
    dispatch(nextRound());
    dispatch(updatePhaseForNewRound())
    dispatch(roundWinner(null))
}

    return(
        <Box>
            <Button onClick={startNextRound}>Next round</Button>
        </Box>
    )
}
