const { createTheme } = require("@mui/system");

export const Colors = {
    primary: "#faf4e9",
    secondary: "#5a5d9b",

    dark: "#4e4d82",
    light: "#7A79B3",

    border: "#DDDFE1",

    shaft: "#333",
    dove_gray: "#d5d5d5",
    body_bg: "#9093C4",

};


 const theme = createTheme({
    palette: {
        primary: {
          main: Colors.primary
        },
        secondary: {
          main: Colors.secondary
        },
      },
      components: {
        MuiButton: {
          defaultProps: {
            disableRipple: true,
            disableElevation: true
          },
        },
        MuiDrawer: {
          styleOverrides: {
            paper: {
              width: 250,
              background: Colors.dark,
              borderRadius: "0px 10px 10px 0px",
              borderRight: `1px solid ${Colors.primary}`,
              color: Colors.white,
            }
          }
        },
        MuiDivider: {
          styleOverrides: {
            root: {
              borderColor: Colors.border,
            }
          }
        },
        MuiAccordion: {
          styleOverrides: {
            root: {
              backgroundColor: Colors.dark,
              boxShadow: 'none'
            }
          }
        },
        MuiAccordionSummary: {
          styleOverrides: {
            content: {
              margin: 0,
              '&$expanded': {
                margin: 0,
              },
            }
          },
        },
        MyDonateButton: {
          styleOverrides: {
            root: {
              color: Colors.black,
              "&:hover": {
                color: Colors.white
              }
            },
            primary: {
              background: Colors.primary,
              "&:hover": {
                background: Colors.primary,
              }
            },
            secondary: {
              background: Colors.secondary
            }
          }
        }
      }
})
export default theme
