import { Button } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { nextRound, roundWinner } from "../../slices/gameSlice";
import { updatePhaseForNewRound } from "../../slices/userSlice";
import { NextRoundButton, NextRoundButtonContainer } from "../../styles/footer";



export default function Footer() {
    const dispatch = useDispatch();
    const [finishGame, setFinishGame] = useState(false)
    const phaseCount = useSelector(state => state.users.phase)

    useEffect(() => {
        if(phaseCount === 10){
            console.log("hello");
        }
    },[phaseCount])

    const startNextRound = () => {
        dispatch(nextRound());
        dispatch(updatePhaseForNewRound())
        dispatch(roundWinner(null))
    }

    return (
        <NextRoundButtonContainer>
            <NextRoundButton
                variant="contained"
                onClick={startNextRound}>
                Next round
            </NextRoundButton>
        </NextRoundButtonContainer>
    )
}
