import { Button, Input } from "@mui/material";
import { Box } from "@mui/system";
import { useDispatch, useSelector } from "react-redux";
import { addUserName } from "../../slices/userSlice";
import { CardsContainer, PlayerContainer } from "../../styles/cards"


export default function Cards() {

    const users = useSelector((state) => state.users);
    let userName = useSelector((state) => state.users)
    const dispatch = useDispatch();

    const changeName = () => {
        dispatch(addUserName)
    }

    return (
        <CardsContainer>
            {users && users.map((user) =>
                <PlayerContainer key={user.id}>
                    <Box>
                        <Input></Input>
                        <Button onClick={changeName}>âˆš</Button>
                    </Box>
                    <div>{user.score}</div>
                    <Button>Win</Button>
                    <Button>Finished phase</Button>
                    <h3>Phase {user.phase}</h3>
                </PlayerContainer>
            )}
        </CardsContainer>
    )
}
