import { Typography } from "@mui/material";
import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { addUser } from "../../slices/userSlice";
import { store } from "../../store";
import { AddPlayerButton, AddPlayerButtonContainer, HeaderContainer, Logo } from "../../styles/header";

export default function Header() {
    const dispatch = useDispatch();
    const [disabledButton, setDisabledButton] = useState(false)

const stateStore = 

    useEffect(() => {
        if (stateStore.users.length === 5) {
            setDisabledButton(true)
        }
    }, [state])
    const addPlayer = async () => {
        let stateStore = await store.getState()
        dispatch(addUser())
        if (stateStore.users.length === 5) {
            setDisabledButton(true)
        }
    }

    return (
        <HeaderContainer>
            <Logo variant="h1">Phase10</Logo>
            <AddPlayerButtonContainer>
                <AddPlayerButton
                    disabled={disabledButton}
                    variant="contained"
                    onClick={addPlayer}>
                    Add player +
                </AddPlayerButton>
            </AddPlayerButtonContainer>
        </HeaderContainer>
    )
}
