import { Grid, List, useMediaQuery } from "@mui/material";
import { useSelector } from "react-redux";
import theme from "../../styles";
import { CardsContainer } from "../../styles/cards"
import { PlayerCard } from "../playerCard/playerCard";
import { store } from "../../store";
import { useEffect, useState } from "react";

export default function Cards() {
    const users = useSelector((state) => state.users);
    const [countOfCols, setCountOfCols] = useState(3);

    useEffect(  () => {
        const stateStore =  store.getState();

        if (stateStore.users.length === 4) {
            setCountOfCols(2)
        }
    }, [store])






    return (
        <CardsContainer>
            <Grid  cols={countOfCols}>

                {users && users.map((user, index) =>
                    <PlayerCard key={user.id} index={index} user={user} />
                )}
            </Grid>
        </CardsContainer>
    )
}
