import { Grid, List, useMediaQuery } from "@mui/material";
import { useSelector } from "react-redux";
import theme from "../../styles";
import { CardsContainer } from "../../styles/cards"
import { PlayerCard } from "../playerCard/playerCard";
import { store } from "../../store";
import { useEffect, useState } from "react";

export default function Cards() {
    const users = useSelector((state) => state.users);
    const [classNameForCards, setClassNameForCards] = useState("")

    useEffect(() => {
        const storeState = store.getState();
        const countOfPlayers = storeState.users.length;

        if (countOfPlayers === 4) {
            setClassNameForCards("player-cards-grid-4-players")
        }
        setClassNameForCards("player-cards-grid")
    }, [store])


    return (
        <CardsContainer container >

            {users && users.map((user, index) =>
                <PlayerCard className={classNameForCards} key={user.id} index={index} user={user} />
            )}
        </CardsContainer>
    )
}
