import { Button, Input } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName } from "../../slices/userSlice";
import { roundWinner } from "../../slices/gameSlice";
import { PlayerContainer } from "../../styles/cards";

export function PlayerCard({ user, index }) {
    const [name, setName] = useState("");
    const users = useSelector((state) => state.users);
    const dispatch = useDispatch();
    const [edit, setEdit] = useState(true);
    const [winner, setWinner] = useState(null);
    const [editingScore, setEditingScore] = useState(null)


    const changeName = (userIndex) => {
        dispatch(addUserName({
            name: name,
            index: userIndex
        }));
        setEdit(false)

    };
    const inputName = (event) => {
        const userName = event.target.value
        return setName(userName)
    };

    const editName = () => {
        setEdit(true);
    };

    const roundWon = (id) => {
        setWinner(id)
        dispatch(roundWinner({
            roundWinner: winner
        }));
        setEditingScore(false)

    };
    const editScore = () => {

    }

    return (
        <PlayerContainer>

            {edit &&
                <Box>
                    <Input onChange={(event) => inputName(event)} value={name}></Input>

                    <Button onClick={() => changeName(index)}>âˆš</Button>
                </Box>
            }
            {!edit &&
                <Box display={"flex"}>
                    <h3>{name}</h3>
                    <Button onClick={() => editName()}>/</Button>
                </Box>
            }

            <div>{user.score}</div>
            {editingScore && <Input> <Button></Button></Input>}
            {!editingScore &&
                <Button onClick={() => roundWon(user.id)}>Win</Button>
            }
            <Button>Finished phase</Button>
            <h3>Phase {user.phase}</h3>

        </PlayerContainer>
    )
}
