import { Button, fabClasses, Input } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName, finishPhaseForUser, updateScore } from "../../slices/userSlice";
import { roundWinner } from "../../slices/gameSlice";
import { PlayerContainer } from "../../styles/cards";

export function PlayerCard({ user, index }) {
    const [name, setName] = useState("");
    const winnerId = useSelector((state) => state.game.roundWinner);
    const dispatch = useDispatch();
    const [edit, setEdit] = useState(true);
    const [score, setScore] = useState(0);
    const [editScore, setEditScore] = useState(false);
    const round = useSelector((state) => state.game.round)
    const currentPhase = useSelector((state) => state.users.phase)
    const finishedPhase = useSelector((state) => state.users.finishedPhase)

useEffect(() => {
    if(currentPhase === 10 && finishedPhase){
        alert("winner")
    }
}, [currentPhase, finishedPhase])

    useEffect(() => {
        setEditScore(false);
    }, [round])

    const changeName = (id) => {
        dispatch(addUserName({
            name: name,
            id: id
        }));
        setEdit(false);
    };

    const inputName = (event) => {
        const userName = event.target.value;
        return setName(userName)
    };

    const editName = () => {
        setEdit(true);
    };

    const roundWon = (id) => {
        dispatch(roundWinner(id));
    };

    const changeScoreForRound = (event) => {
        setScore(parseInt(event.target.value))
    };

    const updateScoreInStore = (id) => {
        dispatch(updateScore({
            id: id,
            score: score
        }))
    };
    const finishPhase = (id) => {
        dispatch(finishPhaseForUser(id));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        updateScoreInStore(user.id);
        setEditScore(true)
    };




    return (
        <PlayerContainer>

            {edit &&
                <Box>
                    <Input
                        type="text"
                        onChange={(event) => inputName(event)} value={name}></Input>

                    <Button onClick={() => changeName(user.id)}>√</Button>
                </Box>
            }
            {!edit &&
                <Box display={"flex"}>
                    <h3>{name}</h3>
                    <Button onClick={() => editName()}>/</Button>
                </Box>
            }

            <div>{user.score}</div>

            {winnerId && winnerId !== user.id &&

                <form onSubmit={handleSubmit} >
                    <Input
                        disabled={editScore}
                        type="text"
                        onChange={(event) => changeScoreForRound(event)} >
                    </Input>
                    <Button type="submit" disabled={editScore} >√</Button>
                </form>


            }
            {(!winnerId || (winnerId && winnerId === user.id)) &&
                <Button onClick={() => roundWon(user.id)}>Win</Button>
            }


            <Button onClick={() => finishPhase(user.id)} disabled={user.finishedPhase}>Finished phase</Button>
            <h3>Phase {user.phase}</h3>

        </PlayerContainer>
    )
}
