import { Button, Input, List, ListItem, TextField, Typography, useMediaQuery } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName, finishPhaseForUser, updateScore } from "../../slices/userSlice";
import { roundWinner } from "../../slices/gameSlice";
import { CardsButtons, CheckMarkButton, CheckMarkContainer, EditIconContainer, EditNameContainer, InputEdit, NameContainer, PlayerContainer, ScoreValue } from "../../styles/cards";
import theme from "../../styles";

export function PlayerCard({ user, index }) {
    const [name, setName] = useState("");
    const [edit, setEdit] = useState(true);
    const [score, setScore] = useState(0);
    const [editScore, setEditScore] = useState(false);

    const winnerId = useSelector((state) => state.game.roundWinner);
    const round = useSelector((state) => state.game.round);

    const dispatch = useDispatch();

    const ifPlayerIsWinner = (winnerId && winnerId !== user.id);

    const matchDownMd = useMediaQuery(theme.breakpoints.down('md'));
    const matchDownXs = useMediaQuery(theme.breakpoints.down('sm'));

    useEffect(() => {
        setEditScore(false);
    }, [round]);



    const changeName = (id) => {
        dispatch(addUserName({
            name: name,
            id: id
        }));
        setEdit(false);
    };

    const inputName = (event) => {
        const userName = event.target.value;
        return setName(userName)
    };

    const editName = () => {
        setEdit(true);
    };



    const roundWon = (id) => {
        dispatch(roundWinner(id));
    };


    const changeScoreForRound = (event) => {
        setScore(parseInt(event.target.value))
    };

    const updateScoreInStore = (id) => {
        dispatch(updateScore({
            id: id,
            score: score
        }))
    };


    const finishPhase = (id) => {
        dispatch(finishPhaseForUser(id));
    };


    const handleSubmit = (e) => {
        e.preventDefault();
        updateScoreInStore(user.id);
        setEditScore(true)
    };




    return (
        <List cols={matchDownXs ? 1 : (matchDownMd ? 2 : 3)}>
            <ListItem>
                <PlayerContainer>
                    {edit ?
                        <EditNameContainer>
                            <InputEdit
                                variant="standard"
                                size="small"
                                onChange={(event) => inputName(event)} value={name}
                                InputProps={{
                                    disableUnderline: true,
                                }}
                            />
                            <Button onClick={() => changeName(user.id)}>
                                <CheckMarkContainer />
                            </Button>
                        </EditNameContainer>
                        :
                        <NameContainer>
                            <Typography sx={{ fontSize: 40 }}>
                                {name}
                            </Typography>
                            <Button onClick={() => editName()}>
                                <EditIconContainer />
                            </Button>
                        </NameContainer>
                    }
                    <ScoreValue>
                        {user.score}
                    </ScoreValue>

                    {ifPlayerIsWinner ?
                        <form onSubmit={handleSubmit}>
                            <InputEdit
                                variant="standard"
                                size="small"
                                disabled={editScore}
                                onChange={(event) => changeScoreForRound(event)}
                                InputProps={{
                                    disableUnderline: true,
                                }}
                            >
                            </InputEdit>
                            <Button type="submit" disabled={editScore}>
                                <CheckMarkContainer />
                            </Button>
                        </form>

                        :
                        <CardsButtons
                            onClick={() => roundWon(user.id)}
                            variant={"contained"}
                        >
                            Win
                        </CardsButtons>
                    }
                    <CardsButtons
                        onClick={() => finishPhase(user.id)}
                        disabled={user.finishedPhase}
                        variant={"contained"}>
                        Finished phase
                    </CardsButtons>
                    <Typography>Phase {user.phase}</Typography>
                </PlayerContainer>
            </ListItem>
        </List>
    )
}
