const { createTheme } = require("@mui/material/styles");

export const Colors = {
    primary: "#faf4e9",
    secondary: "#5a5d9b",

    dark: "#4e4d82",
    light: "#7A79B3",

    border: "#DDDFE1",

    shaft: "#333",
    dove_gray: "#d5d5d5",
    body_bg: "#9093C4",

};

export const overrides = {}

 const theme = createTheme({
    palette: {
        primary: {
          main: Colors.primary
        },
        secondary: {
          main: Colors.secondary
        },
      },
      components: {
        MuiButton: {
          defaultProps: {
            disableRipple: true,
            disableElevation: true,

          },
        },  
      },




})
export default theme
