import { Button } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUser } from "../../slices/userSlice";
import { AddPlayerButton, AddPlayerButtonContainer, CloseInfoBoardButton, HeaderContainer, InfoBoard, InfoButton, InfoContainer, Logo } from "../../styles/header";

export default function Header() {
    const dispatch = useDispatch();
    const [disabledButton, setDisabledButton] = useState(false)
    const [infoIsOpen, setInfoIsOpen] = useState(false)
    const userCount = useSelector(state => state.users.length)

    useEffect(() => {
        if (userCount === 6) {
            setDisabledButton(true)
        } else {
        setDisabledButton(false)}
    }, [userCount]);

    const addPlayer = async () => {
        dispatch(addUser())

    };

    const openInfoBoard = () => {
        setInfoIsOpen(true)
    };
    const closeInfoBoard = () => {
        setInfoIsOpen(false)
    };


    return (
        <>
        <HeaderContainer>
            <Logo variant="h1">
                Phase10
                <Button>
                    <InfoButton onClick={openInfoBoard}/>
                </Button>
                </Logo>
            <AddPlayerButtonContainer>
                <AddPlayerButton
                    disabled={disabledButton}
                    variant="contained"
                    onClick={addPlayer}>
                    Add player +
                </AddPlayerButton>
            </AddPlayerButtonContainer>
        </HeaderContainer>
        {infoIsOpen && 
        <InfoContainer
        open={infoIsOpen}
        onClose={closeInfoBoard}
        hideBackdrop
        disableAutoFocus
        >
            <Box>

            <InfoBoard>
            Phase 1: 2 sets of 3 <br />
            Phase 2: set of 3 + run of 4 <br />
            Phase 3: set of 4 + run of 4 <br />
            Phase 4: run of 7 <br />
            Phase 5: run of 8 <br />
            Phase 6: run of 9 <br />
            Phase 7: 2 sets of 4 <br />
            Phase 8: 7 card of one color <br />
            Phase 9: 1 set of 5 + 1 set of 2 <br />
            Phase 10: 1 set of 5 + 1 set of 3

            </InfoBoard>
            <CloseInfoBoardButton />
            </Box>
        </InfoContainer>
        }
        </>
    )
}
