const { createTheme } = require("@mui/system");

export const Colors = {
    primary: "#faf4e9",
    secondary: "#5a5d9b",

    dark: "#4e4d82",
    light: "#7A79B3",

    border: "#DDDFE1",

    shaft: "#333",
    dove_gray: "#d5d5d5",
    body_bg: "#9093C4",

};


export const theme = createTheme({
    palette: {
        primary: {
            main: Colors.primary
        },
        secondary: {
            main: Colors.secondary
        },
    },
    components: {
        MuiButtonRoot: {
            disableRipple: true,
            disableElevation: true
        }
    },
    typography: {
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
    },
    transitions: {
        duration: {
          shortest: 150,
          shorter: 200,
          short: 250,
          // most basic recommended timing
          standard: 300,
          // this is to be used in complex animations
          complex: 375,
          // recommended when something is entering screen
          enteringScreen: 225,
          // recommended when something is leaving screen
          leavingScreen: 195,
        },
      },
})
theme.transitions.create();
