const { createSlice } = require("@reduxjs/toolkit");


const initialState = [
    {
        id: 1,
        name: "",
        score: 0,
        phase: 1,
        finishedPhase: false
    },
    {
        id: 2,
        name: "",
        score: 0,
        phase: 1,
        finishedPhase: false
    }
];

const usersSlice = createSlice({
    name: "users",
    initialState,
    reducers: {
        addUser(state) {
            const newUser = { id: state.length + 1, score: 0, phase: 1 };
            return [...state, newUser]
        },
        addUserName(state, action) {
            return state.map((item) => item.id === action.payload.id ? { ...item, name: action.payload.name } : item)
        },
        finishPhaseForUser(state, action) {
            return state.map((item) => item.id === action.payload ? { ...item, finishedPhase: true } : item)
        },
        updatePhaseForNewRound(state) {
            return state.map((item) => item.finishedPhase ? { ...item, finishedPhase: false, phase: item.phase + 1 } : item)
        },
        updateScore(state, action) {
            return state.map((item) => item.id === action.payload.id ? { ...item, score: item.score + action.payload.score } : item);
        }

    }
});

export default usersSlice.reducer;
export const { addUser, addUserName, finishPhaseForUser, updatePhaseForNewRound, updateScore } = usersSlice.actions
