import { Modal } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { nextRound, roundWinner } from "../../slices/gameSlice";
import { updatePhaseForNewRound } from "../../slices/userSlice";
import { FinishButton, NextRoundButton, NextRoundButtonContainer } from "../../styles/footer";



export default function Footer() {
    const dispatch = useDispatch();
    const finishGame = useSelector((state) => state.game.finishGame);
    const [finishGameButton, setFinishGameButton] = useState(false);
    const [modalWindow, setModalWindow] = useState(false)

    useEffect(() => {
        if (finishGame === true) {
            setFinishGameButton(true)
        }
    }, [finishGame])

    const startNextRound = () => {
        dispatch(nextRound());
        dispatch(updatePhaseForNewRound())
        dispatch(roundWinner(null))
    };

    const openModalWindow = () => {
        setModalWindow(true)
    };

    const closeModalWindow = () => {
        setModalWindow(false)
    }

    return (
        <NextRoundButtonContainer>
            {!finishGameButton ?
                <NextRoundButton
                    variant="contained"
                    onClick={startNextRound}>
                    Next round
                </NextRoundButton>
                :
                <FinishButton
                    variant="contained"
                    onClick={openModalWindow}
                >
                    Finish game
                </FinishButton>
            }
            <Modal
                open={modalWindow}
                onClose={closeModalWindow}
                sx={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    width: 400,
                    bgcolor: 'pink',
                    border: '2px solid #000',
                    boxShadow: 24,
                    pt: 2,
                    px: 4,
                    pb: 3,
                }}
            >
                <Box>
                    Winner!
                </Box>
            </Modal>
        </NextRoundButtonContainer>
    )
}
