import { Button, Input, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName, finishPhaseForUser, updateScore } from "../../slices/userSlice";
import { roundWinner } from "../../slices/gameSlice";
import { CardsButtons, PlayerContainer } from "../../styles/cards";

export function PlayerCard({ user, index }) {
    const [name, setName] = useState("");
    const [edit, setEdit] = useState(true);
    const [score, setScore] = useState(0);
    const [editScore, setEditScore] = useState(false);

    const winnerId = useSelector((state) => state.game.roundWinner);
    const round = useSelector((state) => state.game.round);

    const dispatch = useDispatch();


    useEffect(() => {
        setEditScore(false);
    }, [round]);



    const changeName = (id) => {
        dispatch(addUserName({
            name: name,
            id: id
        }));
        setEdit(false);
    };

    const inputName = (event) => {
        const userName = event.target.value;
        return setName(userName)
    };

    const editName = () => {
        setEdit(true);
    };



    const roundWon = (id) => {
        dispatch(roundWinner(id));
    };


    const changeScoreForRound = (event) => {
        setScore(parseInt(event.target.value))
    };

    const updateScoreInStore = (id) => {
        dispatch(updateScore({
            id: id,
            score: score
        }))
    };


    const finishPhase = (id) => {
        dispatch(finishPhaseForUser(id));
    };


    const handleSubmit = (e) => {
        e.preventDefault();
        updateScoreInStore(user.id);
        setEditScore(true)
    };




    return (
        <PlayerContainer>

            {edit &&
                <Box>
                    <Input
                        type="text"
                        onChange={(event) => inputName(event)} value={name}></Input>

                    <Button onClick={() => changeName(user.id)}>√</Button>
                </Box>
            }
            {!edit &&
                <Box display={"flex"}>
                    <h3>{name}</h3>
                    <Button onClick={() => editName()}>/</Button>
                </Box>
            }

            <Typography sx={{bgcolor:"black"}}>{user.score}</Typography>

            {winnerId && winnerId !== user.id &&

                <form onSubmit={handleSubmit} >
                    <Input
                        disabled={editScore}
                        type="text"
                        onChange={(event) => changeScoreForRound(event)} >
                    </Input>
                    <Button type="submit" disabled={editScore} >√</Button>
                </form>


            }
            {(!winnerId || (winnerId && winnerId === user.id)) &&
                <CardsButtons onClick={() => roundWon(user.id)} variant={"contained"}>Win</CardsButtons>
            }


            <CardsButtons onClick={() => finishPhase(user.id)}
                disabled={user.finishedPhase}
                variant={"contained"}>
                Finished phase
            </CardsButtons>
            <h2>Phase {user.phase}</h2>

        </PlayerContainer>
    )
}
