import { Button, Input } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName, finishPhaseForUser } from "../../slices/userSlice";
import { roundWinner } from "../../slices/gameSlice";
import { PlayerContainer } from "../../styles/cards";

export function PlayerCard({ user, index }) {
    const [name, setName] = useState("");
    const winnerId = useSelector((state) => state.game.roundWinner);
    const dispatch = useDispatch();
    const [edit, setEdit] = useState(true);

    const changeName = (id) => {
        dispatch(addUserName({
            name: name,
            id: id
        }));
        setEdit(false)

    };
    const inputName = (event) => {
        const userName = event.target.value
        return setName(userName)
    };

    const editName = () => {
        setEdit(true);
    };

    const roundWon = (id) => {
        dispatch(roundWinner(id));
    };

    const finishPhase = (id) => {
        dispatch(finishPhaseForUser(id));
    }

    const updateScoreInStore = (id) => {
        dispatch()
    }


    return (
        <PlayerContainer>

            {edit &&
                <Box>
                    <Input onChange={(event) => inputName(event)} value={name}></Input>

                    <Button onClick={() => changeName(user.id)}>âˆš</Button>
                </Box>
            }
            {!edit &&
                <Box display={"flex"}>
                    <h3>{name}</h3>
                    <Button onClick={() => editName()}>/</Button>
                </Box>
            }

            <div>{user.score}</div>

            {winnerId && winnerId !== user.id &&

                <Input onChange={(id) => updateScoreInStore(id)}></Input>


            }
            {(!winnerId || (winnerId && winnerId === user.id)) &&
                <Button onClick={() => roundWon(user.id)}>Win</Button>
            }


            <Button onClick={() => finishPhase(user.id)} disabled={user.finishedPhase}>Finished phase</Button>
            <h3>Phase {user.phase}</h3>

        </PlayerContainer>
    )
}
