import { Button, Input, List, ListItem, TextField, Typography, useMediaQuery } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUserName, deleteUserCard, finishPhaseForUser, updateScore } from "../../slices/userSlice";
import { roundWinner } from "../../slices/gameSlice";
import { CardsButtons, CheckMarkButton, CheckMarkContainer, DeletePlayerIcon, EditIconContainer, EditNameContainer, InputEdit, Name, NameContainer, PlayerContainer, ScoreValue } from "../../styles/cards";


export function PlayerCard({ user, index }) {
    const [name, setName] = useState("");
    const [edit, setEdit] = useState(true);
    const [score, setScore] = useState(0);
    const [editScore, setEditScore] = useState(false);

    const winnerId = useSelector((state) => state.game.roundWinner);
    const round = useSelector((state) => state.game.round);
    const userCount = useSelector((state) => state.users.length)

    const dispatch = useDispatch();

    const ifPlayerIsWinner = (winnerId && winnerId !== user.id);


    useEffect(() => {
        setEditScore(false);
    }, [round]);



    const changeName = (id) => {
        dispatch(addUserName({
            name: name,
            id: id
        }));
        setEdit(false);
    };

    const inputName = (event) => {
        const userName = event.target.value;
        return setName(userName)
    };

    const editName = () => {
        setEdit(true);
    };

    const deleteUser = (id) => {
        if(userCount > 2){

            dispatch(deleteUserCard(id));
        }
        console.log(id);
    }



    const roundWon = (id) => {
        dispatch(roundWinner(id));
    };


    const changeScoreForRound = (event) => {
        setScore(parseInt(event.target.value))
    };

    const updateScoreInStore = (id) => {
        dispatch(updateScore({
            id: id,
            score: score
        }))
    };


    const finishPhase = (id) => {
        dispatch(finishPhaseForUser(id));
    };


    const handleSubmit = (e) => {
        e.preventDefault();
        updateScoreInStore(user.id);
        setEditScore(true)
    };




    return (
        <>
            <PlayerContainer >

                {edit ?
                    <EditNameContainer>
                        <Button>
                            <DeletePlayerIcon onClick={() => deleteUser(user.id)} />
                        </Button>
                        <InputEdit
                            variant="standard"
                            size="small"
                            onChange={(event) => inputName(event)} value={name}
                            InputProps={{
                                disableUnderline: true,
                            }}
                        />
                        <Button onClick={() => changeName(user.id)}>
                            <CheckMarkContainer />
                        </Button>
                    </EditNameContainer>
                    :
                    <NameContainer>
                        <Button>
                            <DeletePlayerIcon />
                        </Button>
                        <Name sx={{ fontSize: 40 }}>
                            {name}
                        </Name>
                        <Button onClick={() => editName()}>
                            <EditIconContainer />
                        </Button>
                    </NameContainer>
                }
                <ScoreValue>
                    {user.score}
                </ScoreValue>

                {ifPlayerIsWinner ?
                    <form onSubmit={handleSubmit}>
                        <InputEdit
                            variant="standard"
                            size="small"
                            disabled={editScore}
                            onChange={(event) => changeScoreForRound(event)}
                            InputProps={{
                                disableUnderline: true,
                            }}
                        >
                        </InputEdit>
                        <Button type="submit" disabled={editScore}>
                            <CheckMarkContainer />
                        </Button>
                    </form>

                    :
                    <CardsButtons
                        onClick={() => roundWon(user.id)}
                        variant={"contained"}
                    >
                        Win
                    </CardsButtons>
                }
                <CardsButtons
                    onClick={() => finishPhase(user.id)}
                    disabled={user.finishedPhase}
                    variant={"contained"}>
                    Finished phase
                </CardsButtons>
                <Typography>Phase {user.phase}</Typography>
            </PlayerContainer>


        </>
    )
}
