import styled from "@emotion/styled";
import { Modal } from "@mui/material";
import { Colors } from ".";

export const ModalWindowContainer = styled(Modal)(() => ({
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    backgroundColor: Colors.primary,
    border: '2px solid #000',
    boxShadow: 24,
    pt: 2,
    px: 4,
    pb: 3,
}))
